
// App.jsx portion


import React, { useState, useEffect } from 'react';
import BarChart from './components/BarChart';
import LineChart from './components/LineChart';
import ScatterChart from './components/ScatterChart';
import BubbleChart from './components/BubbleChart';


const App = () => {
  const [chartData, setChartData] = useState(null); //stores data from financial_data json
  const [loading, setLoading] = useState(true); //state of loading data

//will fetch the json data 
  useEffect(() => { 
    const fetchData = async () => {
      try {
        const response = await fetch('financial_data.json');
        const data = await response.json();
        
      
        setChartData(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []); 

 //loading screen while json is being fetched
  if (loading) {
    return <div>Loading chart data...</div>;
  }

  
  return (
    <div>
      <h1>Financial Charts</h1>

      {/* Bar Chart for Monthly Sales */}
      <h2>Bar Chart: Monthly Sales</h2>
      <BarChart data={chartData} />

      {/* Line Chart for Monthly Profits */}
      <h2>Line Chart: Monthly Profits</h2>
      <LineChart data={chartData} />

      {/* Scatter Chart for Expenses vs Profits */}
      <h2>Scatter Chart: Expenses vs Profits</h2>
      <ScatterChart data={chartData} />

      {/* Bubble Chart for Combined Metrics (Sales, Profits, Expenses) */}
      <h2>Bubble Chart: Sales, Profits, and Expenses</h2>
      <BubbleChart data={chartData} />
    </div>
  );
};

export default App;
